import{t as o}from"./chunks/tag.6e31b572.js";import{o as a,c as e,F as i,R as s,C as t,a as c,V as l,D as h}from"./chunks/framework.a299805b.js";const d=t("h1",{id:"暗藏玄鸡",tabindex:"-1"},[c("暗藏玄鸡 "),t("a",{class:"header-anchor",href:"#暗藏玄鸡","aria-label":'Permalink to "暗藏玄鸡"'},"​")],-1),u=l('<h2 id="词条解释" tabindex="-1">词条解释 <a class="header-anchor" href="#词条解释" aria-label="Permalink to &quot;词条解释&quot;">​</a></h2><p>暗藏玄鸡也是谐音梗，原本应是暗藏玄机，这个梗的意思是指一些视频、图片或者文章内，出现了有关蔡徐坤的“鸡”梗，而这些梗通常会藏于视频或图片的角落，以及文章内旁敲侧击去暗示大家关于“鸡”的梗，有的则是会在视频内的某一帧播放一段有关“鸡”梗的音频，以此来达到娱乐效果，而这种现象，则被网友称之为暗藏玄鸡。</p><h2 id="相关梗" tabindex="-1">相关梗 <a class="header-anchor" href="#相关梗" aria-label="Permalink to &quot;相关梗&quot;">​</a></h2><p>暗藏玄只因（同义梗）</p><h2 id="对来源-暗藏玄机-的解释" tabindex="-1">对来源“暗藏玄机”的解释 <a class="header-anchor" href="#对来源-暗藏玄机-的解释" aria-label="Permalink to &quot;对来源“暗藏玄机”的解释&quot;">​</a></h2><p>这是一个汉语成语，有写法写作“玄机暗藏”，是由最早的道家术语“玄机”和词语“暗藏”组合而来，意思是指“表面看似平淡无奇的事物，暗地里却隐藏着常人难以发现的玄妙机宜和秘密；而现在由于高度发展的现代社会风云变幻、隐显莫测而逐渐被人们使用且流行开来。（摘自百度百科）</p><blockquote><p><a href="https://baike.baidu.com/item/%E7%8E%84%E6%9C%BA%E6%9A%97%E8%97%8F/3969721" target="_blank" rel="noreferrer">百度百科-玄机暗藏（点击跳转）</a></p></blockquote><h2 id="问题价值" tabindex="-1">问题价值 <a class="header-anchor" href="#问题价值" aria-label="Permalink to &quot;问题价值&quot;">​</a></h2><p>🍅🍅🍅</p>',9),f=JSON.parse('{"title":"暗藏玄鸡","description":"","frontmatter":{"tags":[{"text":"鸡"},{"text":"坤坤"}]},"headers":[],"relativePath":"caixukun/ancangxuanji.md","filePath":"caixukun/ancangxuanji.md"}'),_={name:"caixukun/ancangxuanji.md"},k=Object.assign(_,{setup(m){return(r,p)=>(a(),e("div",null,[d,(a(!0),e(i,null,s(r.$frontmatter.tags,n=>(a(),h(o,{tagName:n.text},null,8,["tagName"]))),256)),u]))}});export{f as __pageData,k as default};
