import{t as o}from"./chunks/tag.a0bd5be8.js";import{o as a,c as e,F as s,R as l,C as t,a as n,V as c,D as h}from"./chunks/framework.233b31c9.js";const u=t("h1",{id:"荔枝",tabindex:"-1"},[n("荔枝 "),t("a",{class:"header-anchor",href:"#荔枝","aria-label":'Permalink to "荔枝"'},"​")],-1),d=c('<h2 id="相对正经的解答" tabindex="-1">相对正经的解答 <a class="header-anchor" href="#相对正经的解答" aria-label="Permalink to &quot;相对正经的解答&quot;">​</a></h2><p>属于植物界被子植物门，木兰纲，无患子目、科，富含葡萄糖、蔗糖、蛋白质、脂肪以及维生素A、B、C等，并含叶酸、精氨酸、色氨酸等各种营养素，外表为偶数羽状复叶，无毛，具柄，侧脉纤细；无花瓣；果核呈果状，近球形。常用食用方法，直接食用，酿制荔枝酒后饮用，与其它菜类混合做成菜肴，如：荔枝虾球，荔枝腰花等。</p><h2 id="和蔡徐坤相关的解答" tabindex="-1">和蔡徐坤相关的解答 <a class="header-anchor" href="#和蔡徐坤相关的解答" aria-label="Permalink to &quot;和蔡徐坤相关的解答&quot;">​</a></h2><p>荔枝通常是指一种水果，但因蔡徐坤的黑粉（<a href="/caixukun/xiaoheizi.html">小黑子</a>）恶意翻译后成为一个梗，原灵感来源于蔡徐坤的真爱粉（<a href="/caixukun/ikun.html">ikun</a>）女大学生的一段为蔡徐坤辩护的语音，因为其发音不那么标准导致理智听起来很像荔枝，因此荔枝在小黑子或与蔡徐坤相关对话中便是理智的意思。</p><div class="tip custom-block"><p class="custom-block-title">使用举例</p><p>小黑子油饼食不食？有没有点荔枝啊？</p></div><h2 id="问题价值" tabindex="-1">问题价值 <a class="header-anchor" href="#问题价值" aria-label="Permalink to &quot;问题价值&quot;">​</a></h2><p>🍅🍅（两颗西红柿）</p>',7),f=JSON.parse('{"title":"荔枝","description":"","frontmatter":{"tags":[{"text":"梗"},{"text":"蔡徐坤"},{"text":"小黑子"},{"text":"植物"}]},"headers":[],"relativePath":"caixukun/lizhi.md","filePath":"caixukun/lizhi.md"}'),_={name:"caixukun/lizhi.md"},b=Object.assign(_,{setup(m){return(r,p)=>(a(),e("div",null,[u,(a(!0),e(s,null,l(r.$frontmatter.tags,i=>(a(),h(o,{tagName:i.text},null,8,["tagName"]))),256)),d]))}});export{f as __pageData,b as default};
