import{t as s}from"./chunks/tag.a0bd5be8.js";import{b as l}from"./chunks/bilibili-player.7bef2d29.js";import{o as e,c as t,F as n,R as c,J as h,C as a,a as r,V as d,D as u}from"./chunks/framework.233b31c9.js";const _=a("h1",{id:"荔枝",tabindex:"-1"},[r("荔枝 "),a("a",{class:"header-anchor",href:"#荔枝","aria-label":'Permalink to "荔枝"'},"​")],-1),m=d('<h2 id="相对正经的解答" tabindex="-1">相对正经的解答 <a class="header-anchor" href="#相对正经的解答" aria-label="Permalink to &quot;相对正经的解答&quot;">​</a></h2><p>属于植物界被子植物门，木兰纲，无患子目、科，富含葡萄糖、蔗糖、蛋白质、脂肪以及维生素A、B、C等，并含叶酸、精氨酸、色氨酸等各种营养素，外表为偶数羽状复叶，无毛，具柄，侧脉纤细；无花瓣；果核呈果状，近球形。常用食用方法，直接食用，酿制荔枝酒后饮用，与其它菜类混合做成菜肴，如：荔枝虾球，荔枝腰花等。</p><h2 id="和蔡徐坤相关的解答" tabindex="-1">和蔡徐坤相关的解答 <a class="header-anchor" href="#和蔡徐坤相关的解答" aria-label="Permalink to &quot;和蔡徐坤相关的解答&quot;">​</a></h2><p>荔枝通常是指一种水果，但因蔡徐坤的黑粉（<a href="/caixukun/xiaoheizi.html">小黑子</a>）恶意翻译后成为一个梗，原灵感来源于蔡徐坤的真爱粉（<a href="/caixukun/ikun.html">ikun</a>）女大学生的一段为蔡徐坤辩护的语音，因为其发音不那么标准导致理智听起来很像荔枝，因此荔枝在小黑子或与蔡徐坤相关对话中便是理智的意思。</p><div class="tip custom-block"><p class="custom-block-title">使用举例</p><p>小黑子油饼食不食？有没有点荔枝啊？</p></div><h2 id="相关视频" tabindex="-1">相关视频 <a class="header-anchor" href="#相关视频" aria-label="Permalink to &quot;相关视频&quot;">​</a></h2>',6),p=a("h2",{id:"问题价值",tabindex:"-1"},[r("问题价值 "),a("a",{class:"header-anchor",href:"#问题价值","aria-label":'Permalink to "问题价值"'},"​")],-1),x=a("p",null,"🍅🍅（两颗西红柿）",-1),N=JSON.parse('{"title":"荔枝","description":"","frontmatter":{"tags":[{"text":"梗"},{"text":"蔡徐坤"},{"text":"小黑子"},{"text":"植物"}]},"headers":[],"relativePath":"caixukun/lizhi.md","filePath":"caixukun/lizhi.md"}'),k={name:"caixukun/lizhi.md"},T=Object.assign(k,{setup(f){return(i,b)=>(e(),t("div",null,[_,(e(!0),t(n,null,c(i.$frontmatter.tags,o=>(e(),u(s,{tagName:o.text},null,8,["tagName"]))),256)),m,h(l,{BV:"BV1oL4y1K7sT"}),p,x]))}});export{N as __pageData,T as default};
