import{b as n}from"./chunks/bilibili-player.a9e13dda.js";import{t as i}from"./chunks/tag.6e31b572.js";import{o as a,c as e,F as s,R as l,J as h,C as t,a as d,V as c,D as _}from"./chunks/framework.a299805b.js";const m=t("h1",{id:"听懂掌声",tabindex:"-1"},[d("听懂掌声 "),t("a",{class:"header-anchor",href:"#听懂掌声","aria-label":'Permalink to "听懂掌声"'},"​")],-1),g=c('<h2 id="词条解释" tabindex="-1">词条解释 <a class="header-anchor" href="#词条解释" aria-label="Permalink to &quot;词条解释&quot;">​</a></h2><p>这个梗原本出自于成功学大师聂枭的讲座，在讲座的过程中总是会说出一句口头禅：“听懂掌声”但实际上讲座讲的没有任何意义，甚至连其基本的意思都摸不到但在场的人听到这句话后总会有节奏的鼓掌，而场上也会放出有节奏的BGM，或许听讲的观众是以不甘落后于他人，不懂装懂的心态从而纷纷鼓掌，我们不知道这些观众是真的懂了还是在装懂。其次，聂枭的讲座可以称为毫无意义，讲起来总是说出一大堆话，比如：“花过多少冤枉钱，决定了你有多少气度，冤枉钱都花两个亿了，办实事的时候拿个十个亿那不是谈笑间吗？”。然后就会接上其经典的一句：“听懂掌声。”</p><h2 id="同类梗-梭哈是一种智慧" tabindex="-1">同类梗（梭哈是一种智慧） <a class="header-anchor" href="#同类梗-梭哈是一种智慧" aria-label="Permalink to &quot;同类梗（梭哈是一种智慧）&quot;">​</a></h2><p>与其对应的同类还有周文强，并且对应的话是“梭哈”和“梭哈是一种智慧”梭哈的本意大多是指在一些赌一件事，赌注全放，一点不留。因此周文强和聂枭被称为成功学界的两位大师，但其本质上是属于一种听上去非常像传销但又不是传销的非常具有鼓动性的讲座，当乐子看看就好，不能相信，好好努力才能成功。</p><h2 id="问题价值" tabindex="-1">问题价值 <a class="header-anchor" href="#问题价值" aria-label="Permalink to &quot;问题价值&quot;">​</a></h2><p>🍅🍅🍅🍅</p><h2 id="相关视频" tabindex="-1">相关视频 <a class="header-anchor" href="#相关视频" aria-label="Permalink to &quot;相关视频&quot;">​</a></h2><p>聂枭名场面-哔哩哔哩</p>',8),q=JSON.parse('{"title":"听懂掌声","description":"","frontmatter":{"tags":[{"text":"聂枭"},{"text":"成功学"},{"text":"沙雕"},{"text":"“传销”"}]},"headers":[],"relativePath":"niexiao/tingdongzhangsheng.md","filePath":"niexiao/tingdongzhangsheng.md"}'),p={name:"niexiao/tingdongzhangsheng.md"},V=Object.assign(p,{setup(u){return(r,x)=>(a(),e("div",null,[m,(a(!0),e(s,null,l(r.$frontmatter.tags,o=>(a(),_(i,{tagName:o.text},null,8,["tagName"]))),256)),g,h(n,{BV:"BV1Pq4y117wo"})]))}});export{q as __pageData,V as default};
